INSERT INTO `creature_text` VALUES ('34143', '11', '0', 'This area will self-destruct in one minute.', '14', '0', '100', '0', '0', '15424', '34278', '0', '');
INSERT INTO `creature_text` VALUES ('34143', '12', '0', 'Self-destruct sequence finalized. Have a nice day.', '14', '0', '100', '0', '0', '15425', '34279', '0', '');
INSERT INTO `creature_text` VALUES ('27227', '0', '0', 'Where... where am I?', '12', '0', '100', '0', '0', '0', '26240', '0', 'Prospector Rokar');
INSERT INTO `creature_text` VALUES ('27227', '1', '0', 'Wait a minute here! You promised to spare me if I helped you. I held up my end o\' the bargain!', '12', '0', '100', '5', '0', '0', '26874', '0', 'Prospector Rokar');
INSERT INTO `creature_text` VALUES ('27227', '2', '0', 'By the Light, Brann\'s alive? I\'d never help you capture him!', '12', '0', '100', '1', '0', '0', '26879', '0', 'Prospector Rokar');
INSERT INTO `creature_text` VALUES ('26666', '0', '0', 'Stay your blade, Chieftain. He may yet have value to us.', '12', '0', '100', '1', '0', '0', '26875', '0', 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES ('26666', '1', '0', 'Remember the dwarven journal that was discovered? Well, this dwarf could be exactly what we need to track down Brann Bronzebeard and capture him.', '12', '0', '100', '1', '0', '0', '26878', '0', 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES ('26666', '2', '0', 'You will if you wish to keep your head attached to your shoulders.', '12', '0', '100', '1', '0', '0', '26880', '0', 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES ('26666', '3', '0', 'Does that satisfy you, Tormak?', '12', '0', '100', '6', '0', '0', '26881', '0', 'Scout Vor\'takh');
INSERT INTO `creature_text` VALUES ('27221', '0', '0', 'Let\'s see if this works.', '12', '0', '100', '1', '0', '0', '26239', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '1', '0', 'You are among the Taunka of Camp Oneqwah, dwarf.', '12', '0', '100', '1', '0', '0', '26251', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '2', '0', 'If you agree to help us against our mutual enemy, you will come to no harm here.', '12', '0', '100', '1', '0', '0', '26252', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '3', '0', 'Think about it, prospector, but make up your mind swiftly. We must act soon.', '12', '0', '100', '25', '0', '0', '26253', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '4', '0', 'I\'ll admit, dwarf, I was pleasantly surprised to see your plan succeed.', '12', '0', '100', '1', '0', '0', '26871', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '5', '0', 'We can\'t just let you return to your companions and we can\'t take you with us...', '12', '0', '100', '1', '0', '0', '26873', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('27221', '6', '0', 'He will live so long as he proves useful. I\'d be careful if I was you, dwarf.', '12', '0', '100', '273', '0', '0', '26882', '0', 'Tormak the Scarred');
INSERT INTO `creature_text` VALUES ('26484', '2', '0', 'By the Light, you killed Limpy Joe and took the meat! How did you know where to find us?', '12', '0', '100', '5', '0', '0', '26639', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '3', '0', 'Well, if you\'ve come to kill me, too, can you at least wait until I\'ve fed Griselda? Poor girl hasn\'t eaten in days.', '12', '0', '100', '20', '0', '0', '26642', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '4', '0', 'Here, girl. I\'ve got one last meal for you before Limpy Joe\'s killer does us in!', '12', '0', '100', '22', '0', '0', '26643', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '5', '0', 'How could you kill poor, defenseless old Joe? And why did you bring the meat back?', '12', '0', '100', '6', '0', '0', '26645', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '6', '0', 'You\'re an enigma, that\'s what you are... And let me tell you, enigmas and I don\'t get along....', '12', '0', '100', '1', '0', '0', '26646', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '7', '0', 'I think you\'d best leave before Griselda decides she\'s in the mood for dessert.', '12', '0', '100', '25', '0', '0', '26647', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '8', '0', 'Wake up, Griselda. Here, girl.', '12', '0', '100', '0', '0', '0', '25700', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '9', '0', 'They\'ll never find us here, will they? We won\'t let them get us like they got Limpy Joe and Flannel McGee!', '12', '0', '100', '5', '0', '0', '25701', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '9', '1', 'No, we escaped from those monsters! They\'re nothing like us, Griselda.', '12', '0', '100', '1', '0', '0', '25702', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '9', '2', 'We\'re hunters, you and I. No wolfmen here, no ma\'am.', '12', '0', '100', '274', '0', '0', '25703', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('26484', '9', '3', 'Keep an eye out for them, girl! Don\'t let them get us!', '12', '0', '100', '25', '0', '0', '25704', '0', 'Hugh Glass');
INSERT INTO `creature_text` VALUES ('14848', '1', '0', 'The Stormpike Graveyard was taken by the Alliance!', '14', '0', '100', '0', '0', '8173', '9057', '3', 'Herald');
