INSERT INTO `creature_text` VALUES ('19228', '0', '4', 'Whoa!  Is all that applause for me?  Thanks for coming out!', '12', '0', '100', '6', '0', '0', '16326', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '47', '0', 'Used to be you couldn\'t trust a goblin...', '12', '0', '100', '1', '0', '0', '16671', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '48', '0', 'Who am I kiddin\'? If you trade with goblins you\'d better have iron plating in your coinpurse.', '12', '0', '100', '6', '0', '0', '16672', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('18166', '0', '0', 'Do not burden A\'dal with mundane questions, $R.  This being\'s will is all that keeps our enemies from crushing this city.', '12', '0', '100', '0', '0', '0', '17238', '0', 'Khadgar');
INSERT INTO `creature_text` VALUES ('18166', '1', '0', 'Show our guest around Shattrath, will you?  Keep an eye out for pickpockets in the Lower City.', '12', '0', '100', '0', '0', '0', '17266', '0', 'Khadgar');
INSERT INTO `creature_text` VALUES ('26527', '0', '0', 'Oh, no! Adventurers, something awful has happened! A colleague of mine has been captured by the Infinite Dragonflight, and they\'re doing something horrible to him! Keeping Arthas is still your highest priority, but if you act fast you could help save a Guardian of Time!', '15', '0', '100', '0', '0', '0', '32670', '3', 'Chromie');
INSERT INTO `creature_text` VALUES ('26527', '1', '0', 'Adventurers, you must hurry! The Guardian of Time cannot last for much longer!', '15', '0', '100', '0', '0', '0', '32678', '3', 'Chromie');
INSERT INTO `creature_text` VALUES ('26527', '2', '0', 'I can barely sense the Guardian of Time! His timeline is fading quickly!', '15', '0', '100', '0', '0', '0', '32679', '3', 'Chromie');
INSERT INTO `creature_text` VALUES ('22273', '0', '0', 'Die, interloper!  Your puny device cannot pierce the dark magics of Hellfire Citadel!', '12', '0', '100', '397', '0', '0', '20020', '0', 'Fel Warden');
