INSERT INTO `creature_text` VALUES ('22366', '0', '0', 'Thank you for allowing me to visit, $N.  You have a very colorful soul, but it\'s a little brighter than I prefer... or I might have stayed longer!', '15', '0', '100', '0', '0', '0', '20154', '0', 'Demoniac Visitation');
INSERT INTO `creature_text` VALUES ('31283', '0', '0', 'Ah, the hand of the Ebon Blade has finally come to claim me. I tremble in fear.', '14', '0', '100', '0', '0', '0', '31959', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '1', '0', 'Hah! That\'s all you\'ve got? I\'m surprised you even made it to the citadel.', '14', '0', '100', '0', '0', '0', '31961', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '2', '0', 'The Lich King has given me power beyond your wildest nightmares. Behold!', '14', '0', '100', '0', '0', '0', '31962', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '3', '0', 'It all ends here.', '14', '0', '100', '0', '0', '0', '31963', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '4', '0', '%s chuckles knowingly.', '16', '0', '100', '0', '0', '0', '31966', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '5', '0', 'The Lich King shall extinquish... every... last... one... of you.', '14', '0', '100', '0', '0', '0', '31967', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31283', '6', '0', 'There you are Darion! I was wondering when you\'d crawl forth!', '14', '0', '100', '0', '0', '0', '31964', '0', 'Orbaz Bloodbane');
INSERT INTO `creature_text` VALUES ('31290', '0', '0', 'Orbaz Bloodbane, self proclaimed hand of the Lich King.', '14', '0', '100', '0', '0', '0', '31975', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '1', '0', 'Couldn\'t wait for me to be out of the way so that you could sieze control?', '14', '0', '100', '0', '0', '0', '31977', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '2', '0', 'You\'ll never have what it takes to lead anything, Orbaz, not even a legion of mindless skeletons.', '14', '0', '100', '0', '0', '0', '31983', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('31290', '3', '0', 'Good riddance.', '14', '0', '100', '0', '0', '0', '31978', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('17843', '0', '0', 'I want all of your trackers out in search of the criminal, Matis the Cruel. I have given trusted agents of the Hand flare guns. Should you or one of your trackers see the flare, it means that an agent has found Matis and needs assistance. And remember, tracker, Matis is to be brought back to me alive.', '12', '7', '100', '1', '0', '0', '14454', '0', 'Vindicator Kuros');
INSERT INTO `creature_text` VALUES ('17875', '0', '0', 'I am at your service, my lord.', '12', '7', '100', '0', '0', '0', '14453', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '1', '0', 'It will be done, my lord.', '12', '7', '100', '1', '0', '0', '14455', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '2', '0', '%s calls forth their bretheren.', '16', '7', '100', '22', '0', '0', '14456', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '3', '0', 'Brothers and sisters, tonight we hunt! A blood elf known as Matis must be brought to justice! The first to find him gets one week\'s vacation aboard the Exodar!', '12', '7', '100', '1', '0', '0', '14457', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17875', '4', '0', 'Onward! The wilds of Bloodmyst await!', '12', '7', '100', '1', '0', '0', '14458', '0', 'Hunter of the Hand');
INSERT INTO `creature_text` VALUES ('17853', '0', '0', 'We\'ve got you now, Matis the Cruel!', '12', '0', '100', '0', '0', '0', '0', '0', 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES ('17853', '1', '0', 'Return to Kuros. I will bring him to Blood Watch.', '12', '7', '100', '0', '0', '0', '14445', '0', 'Tracker of the Hand');
INSERT INTO `creature_text` VALUES ('17664', '0', '0', 'You will never take me alive!', '12', '0', '100', '0', '0', '0', '14447', '0', 'Matis the Cruel');
INSERT INTO `creature_text` VALUES ('17664', '1', '0', '%s falls unconscious.', '16', '0', '100', '0', '0', '0', '14444', '0', 'Matis the Cruel');
INSERT INTO `creature_text` VALUES ('29306', '1', '0', 'What a rush!', '14', '0', '100', '0', '0', '14436', '32540', '0', 'Gal\'darah - Slay 1');
