INSERT INTO `creature_text` VALUES ('29306', '2', '0', 'Even the mighty... can fall.', '14', '0', '100', '0', '0', '14439', '32543', '0', 'Gal\'darah - Death');
INSERT INTO `creature_text` VALUES ('29306', '3', '0', 'Gut them! Impale them!', '14', '0', '100', '0', '0', '14433', '32537', '0', 'Gal\'darah - Summon Rhino 1');
INSERT INTO `creature_text` VALUES ('29306', '3', '1', 'KILL THEM ALL!', '14', '0', '100', '0', '0', '14434', '32538', '0', 'Gal\'darah - Summon Rhino 2');
INSERT INTO `creature_text` VALUES ('29306', '3', '2', 'Say hello to my BIG friend!', '14', '0', '100', '0', '0', '14435', '32539', '0', 'Gal\'darah - Summon Rhino 3');
INSERT INTO `creature_text` VALUES ('29306', '4', '0', 'Ain\'t gonna be nothin\' left after this!', '14', '0', '100', '0', '0', '14431', '32535', '0', 'Gal\'darah - Transform 1');
INSERT INTO `creature_text` VALUES ('29306', '5', '0', 'You wanna see power? I\'m gonna show you power!', '14', '0', '100', '0', '0', '14432', '32536', '0', 'Gal\'darah - Transform 2');
INSERT INTO `creature_text` VALUES ('29306', '6', '0', '$N is impaled!', '41', '0', '100', '0', '0', '0', '30718', '0', 'Gal\'darah - Impale');
INSERT INTO `creature_text` VALUES ('29932', '0', '0', 'Something stirs in the pool of mojo...', '16', '0', '100', '0', '0', '0', '30740', '0', 'Eck the Ferocious');
INSERT INTO `creature_text` VALUES ('29573', '0', '0', '%s gathers its mojo and surges forward!', '16', '0', '100', '0', '0', '0', '30717', '0', 'Drakkari Elemental - Mojo');
INSERT INTO `creature_text` VALUES ('29573', '1', '0', 'As the last remnants of the %s seep into the ground, the altar set into the floor nearby begins to glow faintly.', '16', '0', '100', '0', '0', '0', '31226', '0', 'Drakkari Elemental - Death');
INSERT INTO `creature_text` VALUES ('29394', '0', '0', 'Not yet, Ymiron, I have other plans for you. You will serve me better within Utgarde Pinnacle. And if these insects survive to find you again, you will get the chance to avenge your wife.', '14', '0', '100', '1', '0', '14748', '23803', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('24023', '0', '0', 'Give me your souls that I might slay the enemy!', '14', '0', '100', '0', '0', '13774', '22622', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '1', '0', 'You will not stop my work here!  My husband-king will awaken!', '14', '0', '100', '0', '0', '13775', '22636', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '0', 'The ritual proceeds too slowly. More sacrifices are needed!', '14', '0', '100', '0', '0', '0', '23802', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '1', 'Soon, my love. Soon you will waken and all of Azeroth will know fear!', '14', '0', '100', '0', '0', '0', '23795', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '2', 'Awaken, Ymiron, the time of your continued reign is nigh.', '14', '0', '100', '0', '0', '0', '23796', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '3', 'Double your efforts! King Ymiron will awaken and then he will ascend!', '14', '0', '100', '0', '0', '0', '23799', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '4', 'Sleep no longer, my love. With the might of the Lich King behind you, the world will tremble.', '14', '0', '100', '0', '0', '0', '23797', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('24023', '2', '5', 'Why is this ritual taking so long? Your master promised that your assistance would speed the process!', '14', '0', '100', '0', '0', '0', '23798', '0', 'Queen Angerboda');
INSERT INTO `creature_text` VALUES ('15214', '28', '0', 'Let the battle begin!', '41', '0', '100', '0', '0', '0', '30961', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '27', '0', 'The Battle for Lake Wintergrasp is about to begin!', '41', '0', '100', '0', '0', '0', '30960', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('23780', '2', '0', 'Is that all you\'ve come to say?', '12', '0', '100', '0', '0', '0', '22686', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('23780', '1', '0', 'What fool dares to enter her majesty\'s dominion unannounced?', '14', '0', '100', '0', '0', '0', '22663', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('23780', '0', '0', 'Archers at the ready!  Hold your fire!', '14', '0', '100', '0', '0', '0', '22662', '0', 'High Executor Anselm');
INSERT INTO `creature_text` VALUES ('24041', '3', '0', 'Behold the Vrykul!   A race that has perfected war and destruction to the point of an art form.  Already they\'ve cast their lot with the Lich King!  Their dwellings surround you and their numbers are easily five times yours.', '12', '0', '100', '0', '0', '0', '22722', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '2', '0', 'Arthas is prepared to offer you power beyond your imagination.  The puny army you lead here would pale in comparison to the phalanxes at your command if you returned to the Scourge\'s embrace.', '12', '0', '100', '0', '0', '0', '22671', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '1', '0', 'But... he has learned of your victory over Stormwind\'s North Fleet and thinks you have potential.  Potential to see reason and abandon Sylvanas\' childish rebellion.', '12', '0', '100', '0', '0', '0', '22669', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('24041', '0', '0', 'Ah, but it is you who intrudes on our master\'s territory.  He could wipe you out in an instant for that transgression alone!  Arthas does not have much love or patience for his escaped slaves.', '12', '0', '100', '0', '0', '0', '22665', '0', 'Prince Keleseth');
INSERT INTO `creature_text` VALUES ('23778', '0', '0', 'Keep them split from their main force!', '14', '0', '100', '0', '0', '0', '22382', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('23778', '0', '1', 'Cover fire, now!', '14', '0', '100', '0', '0', '0', '22381', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('23778', '0', '2', 'Keep firing!  Cut off their reinforcements!', '14', '0', '100', '0', '0', '0', '22380', '0', 'Dark Ranger Lyana');
INSERT INTO `creature_text` VALUES ('24252', '0', '0', 'The foot bone\'s connected to the... hip bone!  The hip bone\'s connected to this... other bone!  Hmmmhmmmmhmmm...', '12', '1', '100', '0', '0', '0', '23066', '0', '\"Hacksaw\" Jenny');
INSERT INTO `creature_text` VALUES ('24252', '0', '1', 'It\'s hard work putting these things together!', '12', '1', '100', '0', '0', '0', '23065', '0', '\"Hacksaw\" Jenny');
INSERT INTO `creature_text` VALUES ('28313', '0', '0', 'Longrunner, your fodder... err, people will be a valuable resource against the vrykul. Did I say resource? I meant, \'ally.\' ', '12', '1', '100', '25', '0', '0', '27727', '0', 'Apothecary Scyllis');
INSERT INTO `creature_text` VALUES ('7564', '0', '0', 'I\'m going to carefully mix this - oops! ...Well, I spilled a little, but it\'s ready!', '12', '0', '100', '0', '0', '0', '4548', '0', 'Marin Noggenfogger');
INSERT INTO `creature_text` VALUES ('10978', '0', '0', '%s jumps in fright!', '16', '0', '100', '0', '0', '0', '6301', '0', 'Legacki');
INSERT INTO `creature_text` VALUES ('15214', '31', '0', 'The Horde has defended Wintergrasp Fortress!', '41', '0', '100', '0', '0', '8454', '31405', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '32', '0', 'The Horde has captured Wintergrasp Fortress!', '41', '0', '100', '0', '0', '8454', '27816', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '33', '0', 'The north-eastern keep tower has been damaged!', '41', '0', '100', '0', '0', '0', '31235', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '34', '0', 'The north-eastern keep tower has been destroyed!', '41', '0', '100', '0', '0', '0', '31236', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '35', '0', 'The south-western keep tower has been damaged!', '41', '0', '100', '0', '0', '0', '31233', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '36', '0', 'The south-western keep tower has been destroyed!', '41', '0', '100', '0', '0', '0', '31238', '2', 'Invisible Stalker');
INSERT INTO `creature_text` VALUES ('15214', '37', '0', 'You have reached Rank 1: Corporal', '42', '0', '100', '0', '0', '0', '30650', '0', 'Invisible Stalker - Player');
INSERT INTO `creature_text` VALUES ('15214', '38', '0', 'You have reached Rank 2: First Lieutenant', '42', '0', '100', '0', '0', '0', '30651', '0', 'Invisible Stalker - Player');
INSERT INTO `creature_text` VALUES ('25197', '0', '0', 'There, $N, now you will be able to speak with, and understand, the Winterfin murlocs.', '12', '0', '100', '1', '0', '0', '24428', '0', 'King Mrgl-Mrgl');
INSERT INTO `creature_text` VALUES ('29860', '0', '0', 'SMASH!', '12', '0', '100', '0', '0', '0', '30471', '0', 'Vile');
INSERT INTO `creature_text` VALUES ('29859', '0', '0', 'MRMRFRMRFRMRRRR!', '12', '0', '100', '0', '0', '0', '30472', '0', 'The Leaper');
INSERT INTO `creature_text` VALUES ('29858', '0', '0', 'My dear general, your time has come!', '12', '0', '100', '0', '0', '0', '30470', '0', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('29851', '0', '0', 'You\'re the one that\'s been disrupting things? This should be easy.', '12', '0', '100', '0', '0', '0', '30465', '0', 'General Lightsbane');
INSERT INTO `creature_text` VALUES ('29840', '0', '0', 'Mrrfrmrfrmrrrrr!', '12', '0', '100', '0', '0', '0', '30463', '0', 'The Leaper');
INSERT INTO `creature_text` VALUES ('29840', '1', '0', 'Mrmrmmrmrmrmrm... mrmrmrmr?!', '12', '0', '100', '1', '0', '12937', '30459', '0', 'The Leaper');
INSERT INTO `creature_text` VALUES ('29770', '0', '0', 'Who intrudes upon my ritual?', '12', '0', '100', '0', '0', '0', '30462', '0', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('29770', '1', '0', 'You dare?! Where is Baron Sliver? I would have words with him!', '12', '0', '100', '34', '0', '1173', '30458', '0', 'Lady Nightswood');
INSERT INTO `creature_text` VALUES ('29769', '0', '0', 'Crush... maim... DESTROY!', '12', '0', '100', '0', '0', '0', '30461', '0', 'Vile');
INSERT INTO `creature_text` VALUES ('29769', '1', '0', 'Vile free? Vile love $N!', '12', '0', '100', '34', '0', '1446', '30460', '0', 'Vile');
INSERT INTO `creature_text` VALUES ('32239', '0', '0', 'It is time.  May the Light give us strength.', '12', '0', '100', '0', '0', '0', '32578', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '1', '0', 'Keep your heads down and follow my lead.', '12', '0', '100', '0', '0', '0', '32616', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '2', '0', 'Here it comes.  Stand ready.', '12', '0', '100', '0', '0', '0', '32619', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '3', '0', 'Something\'s wrong... I sense a dark presence.', '12', '0', '100', '0', '0', '0', '32620', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '4', '0', 'The Lich King is here.  May the Light guide our blades.', '12', '0', '100', '0', '0', '0', '32621', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '5', '0', 'You sound a little too confident.  Especially considering the way our last encounter ended.', '12', '0', '100', '25', '0', '0', '32592', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '6', '0', 'That might be, but I don\'t need to stand on holy ground to run that disembodied heart of yours with the Ashbringer.', '12', '0', '100', '1', '0', '0', '32589', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '7', '0', 'The heart... the last remaining vestige of your humanity.  I had to stop it from being destroyed.  I had to see for myself.  And at last I\'m sure...', '12', '0', '100', '1', '0', '0', '32590', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32239', '8', '0', 'Only shadows from the past remain.  There\'s nothing left to redeem!', '12', '0', '100', '0', '0', '0', '32595', '0', 'Highlord Tirion Fordring');
INSERT INTO `creature_text` VALUES ('32311', '0', '0', 'Take courage, crusaders.  You do not fight alone!', '14', '0', '100', '0', '0', '0', '32708', '0', 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES ('32311', '1', '0', 'The Lich King is badly hurt.  We ought to stay behind and finish him.', '12', '0', '100', '0', '0', '0', '32725', '0', 'Koltira Deathweaver');
INSERT INTO `creature_text` VALUES ('32312', '0', '0', 'Quick, through the portal!  He won\'t stay down for long.', '12', '0', '100', '0', '0', '0', '32707', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('32312', '1', '0', 'Patience... we will get our chance soon enough.  Be content that for once, it is Tirion who is in our debt.', '12', '0', '100', '0', '0', '0', '32726', '0', 'Highlord Darion Mograine');
INSERT INTO `creature_text` VALUES ('32184', '0', '0', 'Uninvited guests!  Did you think you\'d go unnoticed inside my dominion?', '12', '0', '100', '1', '0', '0', '32582', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '1', '0', 'I must confess... you were not altogether unexpected.  I hope you find your final resting place... to your liking.', '12', '0', '100', '1', '0', '0', '32585', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '2', '0', 'Last time we met, you had the advantage of fighting on holy ground.  You\'ll find that our situation has been... reversed.', '12', '0', '100', '11', '0', '0', '32588', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '3', '0', 'I call your bluff.  You\'re a paladin after all.  Your obsession with redemption goes beyond the inane.', '12', '0', '100', '1', '0', '0', '32593', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '4', '0', 'You surely wouldn\'t destroy humanity\'s only chance to redeem its most wayward son.  You\'d sooner die!', '12', '0', '100', '1', '0', '0', '32594', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '5', '0', 'Arrrrggggggggggh!!!!', '14', '0', '100', '34', '0', '0', '32694', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32184', '6', '0', 'You... will pay for that, old man.  Slay them all!', '14', '0', '100', '0', '0', '0', '32698', '0', 'The Lich King');
INSERT INTO `creature_text` VALUES ('32241', '0', '0', 'Tirion\'s down!  Defend him with your lives!', '14', '0', '100', '0', '0', '0', '32687', '0', 'Disguised Crusader');
INSERT INTO `creature_text` VALUES ('716', '0', '0', 'I see you misplaced that chapter you worked so hard to put together, $N. Well, you left it right here!', '12', '0', '100', '0', '0', '0', '9022', '0', 'Barnil Stonepot');
INSERT INTO `creature_text` VALUES ('32687', '0', '0', 'There goes the finest $C I have the honor of knowing.', '12', '0', '100', '0', '0', '0', '32866', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '1', '$N, you\'re an inspiration.', '12', '0', '100', '0', '0', '0', '32867', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '2', 'A fine example of a $g man:woman;, that $N.', '12', '0', '100', '0', '0', '0', '32868', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '3', 'You couldn\'t ask for a finer person than $N, I say.', '12', '0', '100', '0', '0', '0', '32869', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '4', '$N, wonderful to see you. Wonderful.', '12', '0', '100', '0', '0', '0', '32870', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32687', '0', '5', '$N! Ah, fantastic to see you out and about. Busy as always, hm?', '12', '0', '100', '0', '0', '0', '32871', '0', 'Linda Ann Kastinglow');
INSERT INTO `creature_text` VALUES ('32692', '0', '0', 'There goes the finest $C I have the honor of knowing.', '12', '0', '100', '0', '0', '0', '32866', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '1', '$N, you\'re an inspiration.', '12', '0', '100', '0', '0', '0', '32867', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '2', 'A fine example of a $g man:woman;, that $N.', '12', '0', '100', '0', '0', '0', '32868', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '3', 'You couldn\'t ask for a finer person than $N, I say.', '12', '0', '100', '0', '0', '0', '32869', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '4', '$N, wonderful to see you. Wonderful.', '12', '0', '100', '0', '0', '0', '32870', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('32692', '0', '5', '$N! Ah, fantastic to see you out and about. Busy as always, hm?', '12', '0', '100', '0', '0', '0', '32871', '0', 'Arcanist Alec');
INSERT INTO `creature_text` VALUES ('25250', '2', '0', 'Your offer will be carefully considered, harbinger.  In the meantime we will make use of your delegation in an advisory role.', '12', '7', '100', '1', '0', '0', '24790', '0', 'General Arlos');
INSERT INTO `creature_text` VALUES ('25301', '0', '0', 'Our troops, general, consist mostly of villagers and peasants.  Good men, but not quite rid of the prejudices and superstitions of their upbringing.  They\'re not ready to fight alongside our more exotic allies.', '12', '7', '100', '1', '0', '0', '24789', '0', 'Counselor Talbot');
INSERT INTO `creature_text` VALUES ('25285', '0', '0', 'Give the word, general.  I will have two regiments at your disposal in a month\'s time.', '12', '7', '100', '0', '0', '0', '24787', '0', 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES ('25285', '1', '0', 'Very well, general.  Should you change your mind, my people will be more than willing to provide military assistance.', '12', '7', '100', '0', '0', '0', '24788', '0', 'Harbinger Vurenn');
INSERT INTO `creature_text` VALUES ('31033', '0', '0', 'There goes the hero of the Vanguard!', '12', '0', '100', '66', '0', '0', '31385', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '1', '%s cheers at you.', '16', '1', '100', '71', '0', '0', '25275', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '2', 'We couldn\'t have done it without you, $g sir:ma\'am;.', '12', '0', '100', '66', '0', '0', '31384', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '3', 'You honor us with your presence, $N.', '12', '0', '100', '66', '0', '0', '31386', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '4', 'Thank you, $N. From the bottom of my heart. Thank you.', '12', '0', '100', '66', '0', '0', '31388', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('31033', '0', '5', 'Call on me anytime, $N.', '12', '0', '100', '66', '0', '0', '31389', '0', 'Crusader of Virtue');
INSERT INTO `creature_text` VALUES ('19610', '0', '0', 'A skunk! Eeeeeeeeeeeeeeeeeew! Don\'t touch me!', '12', '0', '100', '0', '0', '0', '17857', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('23734', '0', '0', 'That should slow the infection down a little.', '12', '7', '100', '1', '0', '0', '22251', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '1', '0', 'I can only hope the treatment will work.', '12', '7', '100', '1', '0', '0', '22828', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '2', '0', 'I just don\'t have the medical supplies I need to properly treat all of you.', '12', '7', '100', '1', '0', '5495', '22829', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '3', '0', 'Oh my, let me change those bandages!', '12', '7', '100', '5', '0', '0', '22250', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '4', '0', 'Don\'t worry, soldier. Yazmina is going to take care of you.', '12', '7', '100', '1', '0', '0', '22830', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23734', '5', '0', '%s shakes her head.', '16', '7', '100', '274', '0', '0', '22831', '0', 'Anchorite Yazmina');
INSERT INTO `creature_text` VALUES ('23728', '0', '0', '%s clears his throat.', '16', '7', '100', '0', '0', '0', '22737', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('23728', '1', '0', 'I look across this base and all I see is fear... Are you gonna be scared when the Lich King\'s armies are sittin\' pretty in Stormwind? Ironforge? NO! You\'re not. And do you know why? Because you\'ll be dead, that\'s why.', '12', '7', '100', '1', '0', '0', '22738', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('23728', '2', '0', 'So when you\'re feeling that tinglin\' sensation at the base of your spine and you\'re \'bout ready to wet your pants, embrace it. Let it enrage you. That fear is the only thing that\'s gonna keep you breathin\'.', '12', '7', '100', '25', '0', '0', '22739', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('23728', '3', '0', 'Am I clear? Do you understand what I\'m sayin\'? I don\'t wanna have to send any more coffins or letters home.', '12', '7', '100', '6', '0', '0', '22740', '0', 'Guard Captain Zorek');
INSERT INTO `creature_text` VALUES ('24852', '0', '0', 'Launching.', '12', '0', '100', '0', '0', '0', '23860', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('24852', '1', '0', 'System failure in 3... 2...', '12', '0', '100', '0', '0', '0', '23930', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('31238', '0', '0', 'Come, Cloudwing. Let\'s do a lap.', '12', '0', '100', '0', '0', '0', '32247', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '0', '1', 'Feeling as restless as I am, Cloudwing? Then let us fly!', '12', '0', '100', '0', '0', '0', '32252', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '0', '2', 'I\'m bored, Cloudwing. Go, go!', '12', '0', '100', '0', '0', '0', '32251', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '1', '0', '%s sighs softly and rolls her shoulders.', '16', '0', '100', '0', '0', '0', '32249', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '1', '1', '%s looks around Krasus\' Landing, bored.', '16', '0', '100', '0', '0', '0', '32244', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('31238', '1', '2', '%s yawns quietly into the palm of her hand.', '16', '0', '100', '0', '0', '0', '32245', '0', 'Hira Snowdawn');
INSERT INTO `creature_text` VALUES ('19610', '1', '0', 'Get it away from me!', '12', '0', '100', '0', '0', '0', '17858', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '2', '0', 'Show us a trick, Ravandwyr!', '12', '0', '100', '0', '0', '0', '17852', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '3', '0', 'Let me outta here! Guys... guys?', '12', '0', '100', '0', '0', '0', '17874', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '4', '0', 'Oh man, he got you good!', '12', '0', '100', '0', '0', '0', '17875', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '5', '0', 'Remember how you\'re always telling us to chill?', '12', '0', '100', '11', '0', '0', '17876', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '6', '0', 'That was the best trick ever! Ok, boys, let\'s go get a drink.', '12', '0', '100', '4', '0', '0', '17853', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '7', '0', 'My eyebrows! You singed my eyebrows!', '12', '0', '100', '22', '0', '0', '20285', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19610', '8', '0', 'Don\'t worry. It\'s an improvement!', '12', '0', '100', '11', '0', '0', '20286', '0', 'Irradiated Worker');
INSERT INTO `creature_text` VALUES ('19217', '0', '0', 'Oh, another show already? Very well then...', '12', '0', '100', '1', '0', '0', '17848', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '1', '0', '%s sighs audibly and glances at Sagan.', '16', '0', '100', '0', '0', '0', '17849', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '2', '0', 'I shall transform my familiar, Sagan, into something truly wondrous!', '12', '0', '100', '25', '0', '0', '17855', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '3', '0', 'And, for my next trick...', '12', '0', '100', '1', '0', '0', '17850', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '4', '0', 'A simple practical joke.', '12', '0', '100', '25', '0', '0', '17877', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '5', '0', 'Thank you for watching, everyone! If you\'d be so kind as to bring your non-irradiated currency to tomorrow\'s performance, I\'d appreciate it.', '12', '0', '100', '2', '0', '0', '17856', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('19217', '6', '0', 'I shall breathe fire!', '12', '0', '100', '25', '0', '0', '17854', '0', 'Ravandwyr');
INSERT INTO `creature_text` VALUES ('28006', '0', '0', 'You think you\'ve won, mortal? Face the unbridled power of Antiok!', '14', '0', '100', '0', '0', '0', '27415', '0', 'Antiok Yell1');
INSERT INTO `creature_text` VALUES ('28006', '0', '1', 'Behold! The Scythe of Antiok!', '14', '0', '100', '0', '0', '0', '27416', '0', 'Antiok Yell2');
INSERT INTO `creature_text` VALUES ('28006', '1', '0', 'Soon, the bones of Galakrond will rise from their eternal slumber and wreak havoc upon this world!', '14', '0', '100', '0', '0', '0', '27406', '0', 'Antiok Yell3');
INSERT INTO `creature_text` VALUES ('28006', '1', '1', 'The Lich King demands more frost wyrms be sent to Angrathar! Meet his demands or face my wrath!', '14', '0', '100', '0', '0', '0', '27408', '0', 'Antiok Yell4');
INSERT INTO `creature_text` VALUES ('28006', '1', '2', 'Faster, dogs! We mustn\'t relent in our assault against the interlopers!', '14', '0', '100', '0', '0', '0', '27405', '0', 'Antiok Yell5');
INSERT INTO `creature_text` VALUES ('28006', '1', '3', 'Attackers are upon us! Let none through to this ancient grave!', '14', '0', '100', '0', '0', '0', '27409', '0', 'Antiok Yell6');
INSERT INTO `creature_text` VALUES ('28006', '1', '4', 'Hear me, minions! Hear your lord, Antiok! Double your efforts or pay the consequences of failure!', '14', '0', '100', '0', '0', '0', '27407', '0', 'Antiok Yell7');
INSERT INTO `creature_text` VALUES ('27996', '0', '1', 'Across this vast expanse rest the bones of my ancestors. Their spirits have ascended to the Chamber of the Aspects.', '12', '0', '100', '0', '0', '0', '27394', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '0', '2', 'Galakrond was the progenitor of dragonkind. It was from Galakrond that the Titans shaped the five Aspects.', '12', '0', '100', '0', '0', '0', '27396', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '0', '3', 'It truly is a shame that the blue dragonflight has chosen such a heartless, cold path. It pains me greatly to do battle with Malygos\'s children.', '12', '0', '100', '0', '0', '0', '27400', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '0', '4', 'The queen and her council have been speaking lately of a disaster at Ulduar. Something has happened in Storm Peaks!', '12', '0', '100', '0', '0', '0', '27399', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '0', '5', 'Today is a good day, friend. Long has it been since I have left Wyrmrest Temple, free to fly across the great Dragonblight.', '12', '0', '100', '0', '0', '0', '27398', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '0', '6', 'What the Scourge has done here will reverberate throughout our world. The aberrations must be stopped!', '12', '0', '100', '0', '0', '0', '27395', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '1', '0', 'You must remember that I have yet to mature into an elder wyrm. I must eat to renew my strength!', '12', '0', '100', '0', '0', '0', '27397', '0', 'WYRMREST VANQUISHER');
INSERT INTO `creature_text` VALUES ('27996', '2', '0', 'I have been called back to Wyrmrest, ally. Farewell!', '12', '0', '100', '0', '0', '0', '27404', '0', 'WYRMREST VANQUISHER');
