INSERT INTO `creature_text` VALUES ('32641', '0', '0', 'Our friendship is OVER!', '12', '0', '100', '6', '0', '0', '33025', '0', 'Drix Blackwrench');
INSERT INTO `creature_text` VALUES ('32641', '0', '1', 'Hey, yea, I\'ve always wanted to be stranded somewhere.', '12', '0', '100', '0', '0', '0', '33024', '0', 'Drix Blackwrench');
INSERT INTO `creature_text` VALUES ('32641', '0', '2', 'Oh, I see how it is... You\'re gonna do me like that, eh?', '12', '0', '100', '0', '0', '0', '33023', '0', 'Drix Blackwrench');
INSERT INTO `creature_text` VALUES ('32641', '0', '3', 'You\'re just gonna leave me here? How am I supposed to get home?', '12', '0', '100', '0', '0', '0', '33026', '0', 'Drix Blackwrench');
INSERT INTO `creature_text` VALUES ('32642', '0', '0', 'Really, mon? Just like dat you be leavin\' Mojodishu behind?', '12', '0', '100', '1', '0', '0', '33027', '0', 'Mojodishu');
INSERT INTO `creature_text` VALUES ('32642', '0', '1', 'Dis bad mojo what you doin\'. Don\'t say Mojodishu didn\'t warn ya!', '12', '0', '100', '0', '0', '0', '33028', '0', 'Mojodishu');
INSERT INTO `creature_text` VALUES ('32642', '0', '2', 'Be dat way den!', '12', '0', '100', '0', '0', '0', '33029', '0', 'Mojodishu');
INSERT INTO `creature_text` VALUES ('32638', '0', '0', 'Hakmud cannot work under these conditions! Goodbye!', '12', '0', '100', '0', '0', '0', '33017', '0', 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES ('32638', '0', '1', 'I thought we were friend, buddy! You leave Hakmud stranded?', '12', '0', '100', '0', '0', '0', '33018', '0', 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES ('32638', '0', '2', 'Thanks, buddy! Thanks for nothing!', '12', '0', '100', '0', '0', '0', '33014', '0', 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES ('32638', '0', '3', 'What is Hakmud supposed to do now, buddy?', '12', '0', '100', '0', '0', '0', '33016', '0', 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES ('32638', '0', '4', 'Why do you treat Hakmud like this, buddy?', '12', '0', '100', '0', '0', '0', '33015', '0', 'Hakmud of Argus <Traveling Trader>');
INSERT INTO `creature_text` VALUES ('32639', '0', '0', 'Goodbye! You were Gnimo\'s best friend ever!', '12', '0', '100', '0', '0', '0', '33020', '0', 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES ('32639', '0', '1', 'Nobody loves Gnimo...', '12', '0', '100', '0', '0', '0', '33019', '0', 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES ('32639', '0', '2', 'Time for Gnimo to shut down.', '12', '0', '100', '0', '0', '0', '33021', '0', 'Gnimo <Adventurous Tinker>');
INSERT INTO `creature_text` VALUES ('19228', '0', '5', 'What a good looking crowd. The dwarven women even shaved.', '12', '0', '100', '21', '0', '0', '16648', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '17', '3', 'Can I get a buff? I\'m dying up here.', '12', '0', '100', '6', '0', '0', '16386', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '17', '4', 'Come on!  These jokes are epic!', '12', '0', '100', '6', '0', '0', '16387', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '28', '4', 'Thank you everyone!  And keep clapping, there is a fly in here and one of you are bound to get it!  Good night!', '12', '0', '100', '21', '0', '0', '16345', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '49', '0', 'Is it just me, or is the Horde about the ugliest enemy we could find?', '12', '7', '100', '6', '0', '0', '16678', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '50', '0', 'Just so you guys don\'t feel left out, all I said was how attractive the blood elves are.  It loses something in the translation.', '12', '1', '100', '6', '0', '0', '16679', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('19228', '0', '6', 'Are you ready to laugh!', '12', '0', '100', '5', '0', '0', '16329', '0', 'Perry Gatner');
INSERT INTO `creature_text` VALUES ('4880', '0', '0', 'Ok, let\'s get started.', '12', '0', '100', '0', '0', '0', '1610', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '1', '0', 'Now let\'s look for the herb.', '12', '0', '100', '0', '0', '0', '1611', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '2', '0', 'Nope, not here...', '12', '0', '100', '0', '0', '0', '1612', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '3', '0', 'There must be one around here somewhere...', '12', '0', '100', '0', '0', '0', '1613', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '4', '0', 'Ah, there\'s one!', '12', '0', '100', '0', '0', '0', '1614', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '5', '0', 'Come, $N!  Let\'s go over there and collect it!', '12', '0', '100', '0', '0', '0', '1615', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '6', '0', '%s picks the herb from the ground.', '16', '0', '100', '0', '0', '0', '1616', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '7', '0', 'Ok, now let\'s get out of here!', '12', '0', '100', '0', '0', '0', '1617', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '8', '0', 'I can make it from here.  Thanks, $N!  And talk to my employer about a reward!', '12', '0', '100', '0', '0', '0', '1618', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '9', '0', '%s disappears back into the swamp.', '16', '0', '100', '0', '0', '0', '1619', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '10', '0', 'Help! I\'m under attack!', '12', '0', '100', '0', '0', '0', '1629', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('4880', '10', '1', 'Help!  The beast is on me!', '12', '0', '100', '0', '0', '0', '1630', '0', '\"Stinky\" Ignatz');
INSERT INTO `creature_text` VALUES ('31333', '0', '0', 'No, my beloved.', '12', '0', '100', '1', '0', '0', '32040', '0', 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES ('31333', '1', '0', 'They must not discover the fate of the young paladin. Not yet.', '12', '11', '100', '5', '0', '0', '32041', '0', 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES ('31333', '2', '0', 'Come to me, $N.', '15', '0', '100', '0', '0', '0', '32048', '0', 'Alexstrasza the Life-Binder to Player');
INSERT INTO `creature_text` VALUES ('31334', '0', '0', 'My Queen, do they know?', '12', '0', '100', '6', '0', '0', '32038', '0', 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES ('31334', '1', '0', '%s nods.', '16', '0', '100', '0', '0', '0', '32046', '0', 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES ('31334', '2', '0', 'They will not.', '12', '0', '100', '1', '0', '0', '32047', '0', 'Korialstrasz to Player');
INSERT INTO `creature_text` VALUES ('28451', '0', '0', 'What a fight you missed, Dorian! It was one for the ages!', '12', '0', '100', '0', '0', '0', '28026', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '1', '0', 'There we were, riding into battle at full speed on the back of Stampy...', '12', '0', '100', '0', '0', '0', '28027', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '2', '0', 'Everything was shaking all over the place. I could hardly steady my gun.', '12', '0', '100', '0', '0', '0', '28028', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '3', '0', 'And then we saw her, the fearsome beast!', '12', '0', '100', '0', '0', '0', '28029', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '4', '0', 'It took all of $N\'s skill to maintain control of Stampy.', '12', '0', '100', '0', '0', '0', '28030', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '5', '0', 'The battle was a haze of blood, lead, and sweat. The broodmother\'s attacks came faster and faster!', '12', '0', '100', '0', '0', '0', '28032', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '6', '0', 'But, in the end, $N and I prevailed through our wits and teamwork. And now we have this glorious trophy as a memento of our victory.', '12', '0', '100', '0', '0', '0', '28033', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28451', '7', '0', 'Do I detect a bit of jealousy, Dorian?', '12', '0', '100', '0', '0', '0', '28036', '0', 'Hemet');
INSERT INTO `creature_text` VALUES ('28376', '0', '0', 'Well, I don\'t know. I was here tending the fire and discussing the drawbacks of cogs with Zootfizzle. That\'s pretty epic, too, in its own way.', '12', '0', '100', '0', '0', '0', '28037', '0', 'Dorian');
INSERT INTO `creature_text` VALUES ('28376', '1', '0', 'It\'s your lucky day, Zootfizzle. $N has brought back more hatchlings for you.', '12', '0', '100', '0', '0', '0', '28892', '0', 'Dorian');
INSERT INTO `creature_text` VALUES ('28376', '2', '0', 'Well, you can always go back to the camp and help Weslex with all his cogs...', '12', '0', '100', '0', '0', '0', '28894', '0', 'Dorian');
INSERT INTO `creature_text` VALUES ('28374', '0', '0', 'I\'m not here to be the keeper of your hatchlings, Dorian. I\'m doing research of my own, as well.', '12', '0', '100', '0', '0', '0', '28893', '0', 'Zootfizzle');
INSERT INTO `creature_text` VALUES ('28374', '1', '0', 'No, not the cogs! Anything but the cogs!', '12', '0', '100', '0', '0', '0', '28895', '0', 'Zootfizzle');
INSERT INTO `creature_text` VALUES ('21315', '0', '0', 'The skies will darken and all will go quiet. Only then will you know the sweet serenity of death...', '12', '0', '100', '0', '0', '0', '19291', '0', 'Ruul the Darkener');
INSERT INTO `creature_text` VALUES ('21315', '1', '0', 'Your world is at an end.', '12', '0', '100', '0', '0', '0', '19290', '0', 'Ruul the Darkener');
INSERT INTO `creature_text` VALUES ('28229', '0', '0', 'More intruders? You\'ll soon learn, no one messes with the Venture Company!', '14', '0', '100', '0', '0', '0', '27611', '0', 'Venture Co. Air Patrol');
INSERT INTO `creature_text` VALUES ('27987', '0', '0', 'Over here, to the southeast. I see they got you too...', '14', '0', '100', '0', '0', '0', '27610', '0', 'Monte Muzzleshot');
INSERT INTO `creature_text` VALUES ('28192', '0', '0', 'WARNING: Missile lock detected. Engaging evasive maneuvers.', '12', '0', '100', '0', '0', '0', '27621', '0', 'Archimage Pentarus Flying Machine');
INSERT INTO `creature_text` VALUES ('17968', '8', '0', 'All of your efforts have been in vain, for the draining of the World Tree has already begun!  Soon the heart of your world will beat no more!', '14', '0', '100', '0', '0', '10986', '20432', '3', 'Archimonde - Intro');
INSERT INTO `creature_text` VALUES ('33662', '0', '0', 'Any luck?', '12', '0', '100', '0', '0', '0', '34184', '0', 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES ('33662', '1', '0', 'Keep at it. If our suspicions are right and this is a transporter, it could prove extremely useful in our assault.', '12', '0', '100', '0', '0', '0', '34186', '0', 'Kirin Tor Battle-Mage to Player');
INSERT INTO `creature_text` VALUES ('33701', '0', '0', 'I heard a story or two of a Lore Keeper in Uldaman that fit your description. Do you serve a similar purpose?', '12', '0', '100', '0', '0', '0', '33702', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '1', '0', 'Frontal defense systems? Is there something I should let Brann know before he has anyone attempt to enter the complex?', '12', '0', '100', '0', '0', '0', '33704', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '2', '0', 'Can you detail the nature of these defense systems?', '12', '0', '100', '0', '0', '0', '33706', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '3', '0', 'Got it. At least we don\'t have to deal with those orbital emplacements.', '12', '0', '100', '0', '0', '0', '33708', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '4', '0', 'Rhydian, make sure you let Brann and Archmage Pentarus know about those defenses immediately.', '12', '0', '100', '0', '0', '0', '33709', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '5', '0', 'And you mentioned an imprisoned entity? What is the nature of this entity and what is its status?', '12', '0', '100', '0', '0', '0', '33710', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33701', '6', '0', 'Yogg-Saron is here? It sounds like we really will have our hands full then.', '12', '0', '100', '0', '0', '0', '33713', '0', 'High Explorer Dellorah to Player');
INSERT INTO `creature_text` VALUES ('33626', '0', '0', 'Not a bit. For the life of me, I can\'t figure out how this thing works.', '12', '0', '100', '0', '0', '0', '34183', '0', 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES ('33626', '1', '0', 'I know, I know... I haven\'t given up yet. Don\'t get your hopes up though, this technology is way beyond me.', '12', '0', '100', '0', '0', '0', '34187', '0', 'Hired Engineer to Player');
INSERT INTO `creature_text` VALUES ('33686', '3', '0', 'Primary defensive emplacements consist of iron constructs and Storm Beacons, which will generate additional constructs as necessary. Secondary systems consist of orbital defense emplacements.', '12', '0', '0', '0', '0', '0', '33712', '0', 'Norgannon SAY_EVENT_4');
INSERT INTO `creature_text` VALUES ('33686', '2', '0', 'Compromise of complex detected, security override enabled - query permitted.', '12', '0', '0', '0', '0', '0', '33707', '0', 'Norgannon SAY_EVENT_3');
INSERT INTO `creature_text` VALUES ('33686', '1', '0', 'Access to the interior of the complex is currently restricted. Primary defensive emplacements are active. Secondary systems are currently non-active.', '12', '0', '0', '0', '0', '0', '33705', '0', 'Norgannon SAY_EVENT_2');
INSERT INTO `creature_text` VALUES ('33686', '0', '0', 'I was constructed to serve as a repository for essential information regarding this complex. My primary functions include communicating the status of the frontal defense systems and assessing the status of the entity that this complex was built to imprison.', '12', '0', '0', '0', '0', '0', '33703', '0', 'Norgannon SAY_EVENT_1');
INSERT INTO `creature_text` VALUES ('33696', '0', '0', '%s nods.', '16', '0', '100', '0', '0', '0', '33715', '0', 'Archmage Rhydian to Player');
INSERT INTO `creature_text` VALUES ('33696', '1', '0', '%s whispers something to Brann.', '16', '0', '100', '0', '0', '0', '33716', '0', 'Archmage Rhydian to Player');
INSERT INTO `creature_text` VALUES ('15990', '13', '0', 'Kel\'Thuzad strikes!', '41', '0', '100', '0', '0', '0', '32803', '0', 'Kel\'Thuzad EMOTE_PHASE_TWO');
INSERT INTO `creature_text` VALUES ('16980', '1', '0', 'Soon we will eradicate the Alliance and Horde. Then the rest of Azeroth will fall before the might of my army.', '14', '0', '100', '0', '0', '14768', '12988', '3', 'The Lich King SAY_DIALOGUE_SAPPHIRON_LICHKING');
INSERT INTO `creature_text` VALUES ('16980', '2', '0', 'Invaders... here?! DESTROY them, Kel\'Thuzad! Naxxramas must not fall!', '14', '0', '100', '0', '0', '14769', '12989', '3', 'The Lich King SAY_DIALOGUE_SAPPHIRON_LICHKING2');
INSERT INTO `creature_text` VALUES ('16980', '3', '0', 'Very well. Warriors of the frozen wastes, rise up! I command you to fight, kill and die for your master! Let none survive!', '14', '0', '100', '0', '0', '14770', '12994', '3', 'The Lich King SAY_ANSWER_REQUEST');
INSERT INTO `creature_text` VALUES ('15384', '0', '0', 'A Guardian of Icecrown enters the fight!', '41', '0', '100', '0', '0', '0', '32804', '0', 'OLDWorld Trigger (DO NOT DELETE)');
INSERT INTO `creature_text` VALUES ('27447', '4', '0', 'Intruders, your victory will be short-lived. I am Commander Varos Cloudstrider. My drakes control the skies and protect this conduit. I will see to it personally that the Oculus does not fall into your hands!', '14', '0', '100', '0', '0', '13648', '31812', '3', 'Varos Cloudstrider SAY_VAROS_INTRO_TEXT');
INSERT INTO `creature_text` VALUES ('32451', '0', '0', 'She said to meet her right here... maybe I\'m in the wrong place?', '12', '0', '100', '1', '0', '0', '33069', '0', 'Dalaran Citizen');
INSERT INTO `creature_text` VALUES ('32451', '0', '1', 'Luckily, the Lich King\'s grasp has not reached Dalaran yet.', '12', '0', '100', '1', '0', '0', '33068', '0', 'Dalaran Citizen');
INSERT INTO `creature_text` VALUES ('32451', '0', '2', 'Sometimes it\'s nice to get away from the sanctum and get some fresh air.', '12', '0', '100', '1', '0', '0', '33067', '0', 'Dalaran Citizen');
