INSERT INTO `creature_text` VALUES ('25496', '0', '1', 'The subjugation of these people has only just begun... so much time for fun and games.', '12', '0', '50', '53', '0', '0', '24718', '0', 'Kvaldir Mist Lord');
INSERT INTO `creature_text` VALUES ('25496', '0', '2', 'Burn, land dweller! BURN!', '12', '0', '50', '53', '0', '0', '24717', '0', 'Kvaldir Mist Lord');
INSERT INTO `creature_text` VALUES ('25801', '0', '0', 'Your impetuousness will be your end!', '12', '0', '100', '0', '0', '0', '25103', '0', 'Nedar');
INSERT INTO `creature_text` VALUES ('25801', '0', '1', 'You dare challenge Nedar, lord of the tundral!?', '12', '0', '100', '0', '0', '0', '25102', '0', 'Nedar');
INSERT INTO `creature_text` VALUES ('25801', '0', '2', 'You don\'t stand a chance!', '12', '0', '100', '0', '0', '0', '25104', '0', 'Nedar');
INSERT INTO `creature_text` VALUES ('20802', '1', '0', 'By the second sun of K\'aresh, look at this place! I can only imagine what Salhadaar is planning. Come on, let\'s keep going.', '12', '0', '100', '0', '0', '0', '18433', '0', 'Protectorate Demolitionist - Waypoint 1');
INSERT INTO `creature_text` VALUES ('20802', '2', '0', 'Look there, fleshling! Salhadaar\'s conduits! He\'s keeping well fed...', '12', '0', '100', '0', '0', '0', '18435', '0', 'Protectorate Demolitionist - Waypoint 4');
INSERT INTO `creature_text` VALUES ('20802', '3', '0', 'Alright, keep me protected while I plant this disruptor. This shouldn\'t take very long..', '12', '0', '100', '0', '0', '0', '18436', '0', 'Protectorate Demolitionist - Waypoint 6');
INSERT INTO `creature_text` VALUES ('20802', '4', '0', 'Done! Back up! Back up!', '12', '0', '100', '0', '0', '0', '18437', '0', 'Protectorate Demolitionist - Disruptor');
INSERT INTO `creature_text` VALUES ('20802', '5', '0', 'Looks like my work here is done. Report to the holo-image of Ameer over at the transporter.', '12', '0', '100', '0', '0', '0', '18442', '0', 'Protectorate Demolitionist - Waypoint 7');
INSERT INTO `creature_text` VALUES ('20802', '6', '0', 'I\'m under attack! I repeat, I am under attack!', '12', '0', '100', '0', '0', '0', '18439', '0', 'Protectorate Demolitionist - Being attacked');
