INSERT INTO `creature_text` VALUES ('30399', '0', '0', 'It is time I put an end to my brother\'s madness.', '12', '0', '100', '0', '0', '0', '31078', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30399', '1', '0', 'Loken!!', '14', '0', '100', '15', '0', '0', '31079', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30399', '2', '0', 'Stand up and fight me, you worthless traitor!  ', '14', '0', '100', '53', '0', '0', '31080', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30399', '3', '0', 'Do not dare...', '12', '0', '100', '0', '0', '0', '31083', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30399', '4', '0', 'Speak...', '12', '0', '100', '0', '0', '0', '31116', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30399', '5', '0', 'Her name!!', '12', '0', '100', '0', '0', '0', '31117', '0', 'Thorim');
INSERT INTO `creature_text` VALUES ('30396', '0', '0', 'You seem eager to join your beloved Sif, brother.', '12', '0', '100', '0', '0', '0', '31082', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '1', '0', 'ENOUGH!', '14', '0', '100', '15', '0', '0', '31126', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '2', '0', 'Looks like you can still best me in a fair fight, little brother.', '12', '0', '100', '1', '0', '0', '31110', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '3', '0', 'Unfortunately for you.... this fight is anything but fair!', '12', '0', '100', '1', '0', '0', '31111', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '4', '0', 'Thanks to your little friend, you\'ve left the Temple of Storms, where you were at your strongest.  Instead we fight closer to my master\'s power.', '12', '0', '100', '0', '0', '0', '31112', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '5', '0', 'Minions!  Take my brother and his drake to their new dwellings.  Make sure the beast makes it alive.  I have special plans for the broodmother.', '12', '0', '100', '0', '0', '0', '31128', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '6', '0', 'As for your life, mortal.  I will be generous.  After all... why would I destroy my most useful servant?  I waited for you for weeks inside that Hyldnir mine.', '12', '0', '100', '0', '0', '0', '31127', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('30396', '7', '0', 'The shape wasn\'t mine, of course.  And had you not been so reckless, you would\'ve seen past my illusion.  But you came through for me, and for that... I must thank you!', '12', '0', '100', '0', '0', '0', '31133', '0', 'Loken');
INSERT INTO `creature_text` VALUES ('18475', '0', '0', 'Come out, little birdies! Mug\'gok want to play!', '14', '0', '100', '0', '0', '0', '15463', '0', 'Muggok');
INSERT INTO `creature_text` VALUES ('18475', '1', '0', 'No hurt Mug\'gok\'s feelings, bird-men!', '14', '0', '100', '0', '0', '0', '15464', '0', 'Muggok');
INSERT INTO `creature_text` VALUES ('18475', '2', '0', 'Fine! Birdies be dat way! Mug\'gok find new friends!', '14', '0', '100', '14', '0', '0', '15465', '0', 'Muggok');
INSERT INTO `creature_text` VALUES ('10948', '0', '0', 'Darrowshire, to arms! The Scourge approach!', '14', '0', '100', '0', '0', '0', '7358', '0', 'Defender1');
INSERT INTO `creature_text` VALUES ('10944', '0', '0', 'Do not lose hope, Darrowshire! We will not fall!', '14', '0', '100', '0', '0', '0', '7343', '0', 'Lightfire1');
INSERT INTO `creature_text` VALUES ('10946', '0', '0', 'The light burns bright in you, Davil, I will enjoy snuffing it out.', '14', '0', '100', '0', '0', '0', '7344', '0', 'Horgus');
INSERT INTO `creature_text` VALUES ('10944', '1', '0', 'Horgus, your nightmare ends! Now!', '14', '0', '100', '0', '0', '0', '7346', '0', 'Lightfire2');
INSERT INTO `creature_text` VALUES ('10948', '1', '0', 'Horgus is slain! Take heat, defenders of Darrowshire.', '14', '0', '100', '0', '0', '0', '7368', '0', 'Defender2');
INSERT INTO `creature_text` VALUES ('10944', '2', '0', 'Ah! My wounds are too severe. Defenders, fight on without me!', '12', '0', '100', '0', '0', '0', '7227', '0', 'Lightfire2');
INSERT INTO `creature_text` VALUES ('10944', '3', '0', 'Davil Lightfire has succumbed to his wounds!', '16', '0', '100', '0', '0', '0', '7367', '0', 'Lightfire2');
INSERT INTO `creature_text` VALUES ('10937', '0', '0', 'Defenders of Darrowshire! Rally! we must prevail!', '14', '0', '100', '0', '0', '0', '7355', '0', 'Redpath1');
INSERT INTO `creature_text` VALUES ('10945', '1', '0', 'Captain Redpath has appeared on the battlefield! Protect him!', '41', '0', '100', '0', '0', '0', '45683', '0', 'Redpath2');
INSERT INTO `creature_text` VALUES ('10945', '0', '0', 'Captain Redpath!  How could you forsake us!', '41', '0', '100', '0', '0', '0', '7212', '0', 'Redpath3');
INSERT INTO `creature_text` VALUES ('10945', '2', '0', 'Protect Davil Lightfire!', '41', '0', '100', '0', '0', '0', '45681', '0', 'Lightfire1');
INSERT INTO `creature_text` VALUES ('10945', '3', '0', 'Davil Lightfire is defeated!  Darrowshire is lost!', '41', '0', '100', '0', '0', '0', '7366', '0', 'Redpath4');
INSERT INTO `creature_text` VALUES ('10939', '0', '0', 'Redpath! Your life ends, here and now!', '12', '0', '100', '0', '0', '0', '7471', '0', 'Marduk1');
INSERT INTO `creature_text` VALUES ('10938', '0', '0', 'Darrowshire! Your are doomed!', '14', '0', '100', '0', '0', '0', '7356', '0', 'CorruptedRed1');
INSERT INTO `creature_text` VALUES ('10938', '1', '0', 'Captain Joseph Redpath has been corrupted! Kill him quickly!', '41', '0', '100', '0', '0', '0', '45684', '0', 'CorruptedRed2');
INSERT INTO `creature_text` VALUES ('10948', '2', '0', 'Captain Redpath is slain!', '14', '0', '100', '0', '0', '0', '7369', '0', 'Defender3');
INSERT INTO `creature_text` VALUES ('10948', '3', '0', 'The Scourge are defeated! Darrowshire is saved!', '14', '0', '100', '0', '0', '0', '7407', '0', 'Defender4');
INSERT INTO `creature_text` VALUES ('10948', '4', '0', 'Speak with Joseph Redpath in the center of Darrowshire.', '41', '0', '100', '0', '0', '0', '45685', '0', 'Defender5');
INSERT INTO `creature_text` VALUES ('10936', '0', '0', 'Pamela? Are you there, honey?', '12', '0', '100', '0', '0', '0', '7397', '0', 'Joseph1');
INSERT INTO `creature_text` VALUES ('10926', '0', '0', 'Daddy!', '12', '0', '100', '0', '0', '0', '7399', '0', 'Pamela1');
INSERT INTO `creature_text` VALUES ('10926', '1', '0', 'Daddy! You\'re back!', '12', '0', '100', '0', '0', '0', '7400', '0', 'Pamela2');
INSERT INTO `creature_text` VALUES ('10926', '2', '0', 'Let\'s go play! No, tell me a story, Daddy! No... let\'s go pick flowers! And play tea time! I found my dollie, did I tell you?', '12', '0', '100', '0', '0', '0', '7401', '0', 'Pamela3');
INSERT INTO `creature_text` VALUES ('10926', '3', '0', 'I missed you so much, Daddy!', '12', '0', '100', '0', '0', '0', '7402', '0', 'Pamela4');
INSERT INTO `creature_text` VALUES ('10936', '1', '0', 'I missed you too, honey. And I\'m finally home...', '12', '0', '100', '0', '0', '0', '7403', '0', 'Joseph2');
INSERT INTO `creature_text` VALUES ('22862', '0', '3', 'Pathetic worm... your master\'s days are over!', '12', '0', '100', '0', '0', '0', '20671', '0', 'Caalen');
INSERT INTO `creature_text` VALUES ('22862', '0', '4', 'For Xi\'ri!  For the Sha\'tar!', '12', '0', '100', '0', '0', '0', '20667', '0', 'Caalen');
INSERT INTO `creature_text` VALUES ('22862', '0', '5', 'Your end is at hand, Illidari!', '12', '0', '100', '0', '0', '0', '20668', '0', 'Caalen');
INSERT INTO `creature_text` VALUES ('23152', '0', '0', 'Pitiful wretches. You dared invade Illidan\'s temple? Very well, I shall make it your death bed!', '14', '0', '100', '0', '0', '0', '20719', '0', 'Vagath');
INSERT INTO `creature_text` VALUES ('22990', '0', '0', 'Now is the time, Maiev! Unleash your wrath!', '14', '0', '100', '0', '0', '0', '20742', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('22989', '0', '0', 'I\'ve waited for this moment for years. Illidan and his lapdogs will be destroyed!', '14', '0', '100', '0', '0', '0', '20743', '0', 'Maiev');
INSERT INTO `creature_text` VALUES ('22990', '1', '0', 'Slay all who see us! Word must not get back to Illidan.', '14', '0', '100', '0', '0', '0', '20744', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('22989', '1', '0', 'Meet your end, demon!', '12', '0', '100', '0', '0', '0', '0', '0', 'Maiev');
INSERT INTO `creature_text` VALUES ('23152', '1', '0', 'You\'ve sealed you fate, Akama. The Master will learn from your betrayal!', '14', '0', '100', '0', '0', '0', '20745', '0', 'Vagath');
INSERT INTO `creature_text` VALUES ('22990', '2', '0', 'Akama has no master, not anymore.', '12', '0', '100', '0', '0', '0', '20746', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('22990', '3', '0', 'Our plans are in danger already. It appears Maiev\'s decided to do things her own way.', '12', '0', '100', '0', '0', '0', '20748', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('22990', '4', '0', '%s\'s attention shifts to a crack along the temple\'s southern wall.', '16', '0', '100', '0', '0', '0', '20749', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('22990', '5', '0', 'We must carry on with or without Maiev. Inside! Quickly!', '12', '0', '100', '0', '0', '0', '20750', '0', 'Akama');
INSERT INTO `creature_text` VALUES ('18528', '0', '0', '%s falls silent and a quiet tension falls over nearby Sha\'tar forces as Xi\'ri makes his decision.', '16', '0', '100', '0', '0', '0', '20737', '0', 'Xiri');
INSERT INTO `creature_text` VALUES ('18528', '1', '0', '%s begins channeling the powers of the light.', '16', '0', '100', '0', '0', '0', '20693', '0', 'Xiri');
INSERT INTO `creature_text` VALUES ('24823', '0', '0', 'Model U-9207 Iron Rune Construct does not appreciate your tone of voice. Commencing total annihilation of your rug, Lebronski.', '12', '0', '100', '0', '0', '0', '23892', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('24807', '0', '0', 'Behold my perfect creation!', '12', '7', '100', '0', '0', '0', '23873', '0', 'Walt');
INSERT INTO `creature_text` VALUES ('24717', '0', '0', 'WHOA, WHOA, WHOA! Hold everything fellas!', '12', '7', '100', '0', '0', '0', '23875', '0', 'Stanwad');
INSERT INTO `creature_text` VALUES ('24806', '0', '0', 'Launching.', '12', '0', '100', '0', '0', '0', '23860', '0', 'Iron Rune Construct');
INSERT INTO `creature_text` VALUES ('24717', '1', '0', 'Oh dear lords of stone and steel, what... I repeat... WHAT are ya gonna do with that?', '12', '7', '100', '0', '0', '0', '23878', '0', 'Stanwad');
INSERT INTO `creature_text` VALUES ('24717', '2', '0', 'The dwarves all burst out in laughter.', '16', '7', '100', '0', '0', '0', '23876', '0', 'Stanwad');
INSERT INTO `creature_text` VALUES ('24719', '0', '0', 'I\'m cryin\' here! Please, make the funny stop!', '12', '7', '100', '0', '0', '0', '23885', '0', 'Gwendolyn');
INSERT INTO `creature_text` VALUES ('24720', '0', '0', 'If only our pappy were alive to see this!', '12', '0', '100', '0', '0', '0', '23889', '0', 'Needlemeyer');
INSERT INTO `creature_text` VALUES ('24718', '0', '0', 'Man, that will never work. The iron dwarves are never gonna fall for that hunk o\' junk!', '12', '7', '100', '0', '0', '0', '23880', '0', 'Lebronski');
INSERT INTO `creature_text` VALUES ('24807', '1', '0', 'You\'re out of your element, Lebronski! Uncalled for! Those iron dwarves won\'t know what hit \'em! Now why don\'t you get back to uncovering the mystery of the gigantic turd on your rug and leave the real scientists to their work! ', '12', '7', '100', '0', '0', '0', '23883', '0', 'Walt');
INSERT INTO `creature_text` VALUES ('25784', '0', '0', 'I thank you, friend of Dalaran.', '12', '0', '100', '0', '0', '0', '24995', '0', 'Archmage Evanor');
INSERT INTO `creature_text` VALUES ('25784', '1', '0', 'We mustn\'t tarry here... stay close - I\'ll take us somewhere safe.', '12', '0', '100', '0', '0', '0', '24996', '0', 'Archmage Evanor');
INSERT INTO `creature_text` VALUES ('24381', '0', '0', 'Arise from the earth, my brothers!', '12', '0', '100', '0', '0', '0', '23311', '0', 'Megalithe');
INSERT INTO `creature_text` VALUES ('24381', '1', '0', 'Our iron masters have a mission for us!', '12', '0', '100', '0', '0', '0', '23312', '0', 'Megalithe');
INSERT INTO `creature_text` VALUES ('24381', '2', '0', 'Follow me into the mountains to carry out the plan of the masters!', '12', '0', '100', '0', '0', '0', '23315', '0', 'Megalithe');
INSERT INTO `creature_text` VALUES ('24381', '3', '0', 'We will not fail!', '12', '0', '100', '0', '0', '0', '23332', '0', 'Megalithe');
INSERT INTO `creature_text` VALUES ('24847', '0', '0', 'The sound of thunder roars throughout the cavern.', '41', '9', '100', '0', '0', '0', '23927', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '1', '0', 'Come, minions! Your master calls!', '14', '9', '100', '0', '0', '0', '23922', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '2', '0', 'The work of your brothers in the Storm Peaks has reached a frenzied pace. Soon, Father will be freed.', '14', '9', '100', '0', '0', '0', '23923', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '3', '0', 'Your brothers to the north have subjugated the stone behemoths and set them on the path to Ulduar. Soon, the remaining defenses of the Pantheon will be neutralized.', '14', '9', '100', '0', '0', '0', '23924', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '4', '0', 'The three fissures opened by the mortals of this region must be silenced. The earth hears and sees what we do and will continue to interfere with our efforts if the way is not closed.', '14', '9', '100', '0', '0', '0', '23925', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '5', '0', 'Bury this place! Your master demands it!', '14', '9', '100', '0', '0', '0', '23926', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24847', '6', '0', 'The sound of thunder dissipates.', '41', '9', '100', '0', '0', '0', '23928', '0', 'LOKEN');
INSERT INTO `creature_text` VALUES ('24825', '0', '0', 'Alert! Alert! Construct senses that enemies have breeched our defenses. Increase pace.', '12', '9', '100', '0', '0', '0', '23895', '0', 'GOLEM');
INSERT INTO `creature_text` VALUES ('24825', '0', '1', 'Construct class carrier reporting for duty. Primary function: Overseeing.', '12', '9', '100', '0', '0', '0', '23896', '0', 'GOLEM');
INSERT INTO `creature_text` VALUES ('24825', '0', '2', 'There is nothing to see here. Proceed with your mandated duties.', '12', '9', '100', '0', '0', '0', '23897', '0', 'GOLEM');
INSERT INTO `creature_text` VALUES ('24825', '0', '3', 'To interfere with construct\'s function results in termination. Move along.', '12', '9', '100', '0', '0', '0', '23899', '0', 'GOLEM');
INSERT INTO `creature_text` VALUES ('24825', '0', '4', 'Construct must not be questioned by iron dwarf servant. Commence deconstruction.', '12', '9', '100', '0', '0', '0', '23900', '0', 'GOLEM');
INSERT INTO `creature_text` VALUES ('24825', '0', '5', 'Construct is not authorized to communicate with lesser beings. Being must double their efforts.', '12', '9', '100', '0', '0', '0', '23901', '0', 'GOLEM');
