INSERT INTO `creature_text` VALUES ('22431', '1', '0', 'Prepare yourself. Do not allow the ritual to be interrupted or we may lose our patient...', '12', '0', '100', '0', '0', '0', '20401', '0', 'Barada');
INSERT INTO `creature_text` VALUES ('22431', '2', '0', 'Back foul beings of darkness! You have no power here!', '12', '0', '100', '0', '0', '0', '20404', '0', 'Barada');
