INSERT INTO `creature_text` VALUES ('14873', '4', '0', 'This is a shiney rock. I\'ll put it in my bucket. Ok, I just need a few more...', '12', '1', '100', '1', '0', '0', '10279', '0', 'Okla');
INSERT INTO `creature_text` VALUES ('14873', '5', '0', 'I bet my friend Gruk would like this rock for the spear he is making! It\'s very sharp... I hope he likes it.', '12', '1', '100', '1', '0', '0', '10280', '0', 'Okla');
