INSERT INTO `creature_text` VALUES ('3946', '3', '0', 'I have failed... I have failed in my duty... failed my people... The worgen run rampant, and the Scythe of my goddess is lost.', '12', '7', '100', '0', '0', '0', '1359', '0', 'Velinde Starsong');
INSERT INTO `creature_text` VALUES ('3946', '4', '0', 'This evil I have unleashed... In whose hand does the power my goddess granted to me now lie... By whose hand is it directed?', '12', '7', '100', '0', '0', '0', '1360', '0', 'Velinde Starsong');
INSERT INTO `creature_text` VALUES ('3984', '0', '0', 'Sidney is late. I thought he would be home by now.', '12', '7', '100', '0', '0', '0', '1365', '0', 'Nancy Vishas');
INSERT INTO `creature_text` VALUES ('3985', '0', '0', 'He\'s your husband, woman. How am I to know where he is?', '12', '7', '100', '0', '0', '0', '1366', '0', 'Grandpa Vishas');
INSERT INTO `creature_text` VALUES ('3984', '1', '0', 'He must be overwhelmed with work at the Monastery. But still, he should have returned by now.', '12', '7', '100', '0', '0', '0', '1367', '0', 'Nancy Vishas');
INSERT INTO `creature_text` VALUES ('3985', '1', '0', 'Cleansing the world of impure scum isn\'t an easy job, Nancy. Someone\'s got to do it.', '12', '7', '100', '0', '0', '0', '1368', '0', 'Grandpa Vishas');
INSERT INTO `creature_text` VALUES ('3984', '2', '0', 'Well, I\'m going outside to check. Something is amiss.', '12', '7', '100', '0', '0', '0', '1369', '0', 'Nancy Vishas');
INSERT INTO `creature_text` VALUES ('24314', '0', '0', 'So then we too are cursed?', '12', '0', '100', '1', '0', '0', '23116', '0', 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES ('24314', '1', '0', '%s points to the infant.', '16', '0', '100', '0', '0', '0', '23117', '0', 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES ('24315', '0', '0', '%s sobs.', '16', '0', '100', '0', '0', '0', '23118', '0', 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES ('24314', '2', '0', 'The gods have forsaken us! We must dispose of it before Ymiron is notified!', '12', '0', '100', '53', '0', '0', '23119', '0', 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES ('24315', '1', '0', 'NO! You cannot! I beg of you! It is our child!', '12', '0', '100', '0', '0', '0', '23120', '0', 'Ancient Female Vrykul');
INSERT INTO `creature_text` VALUES ('24314', '3', '0', 'Then what are we to do, wife? The others cannot find out. Should they learn of this aberration, we will all be executed.', '12', '0', '100', '1', '0', '0', '23121', '0', 'Ancient Male Vrykul');
INSERT INTO `creature_text` VALUES ('24315', '2', '0', 'I... I will hide it. I will hide it until I find it a home, far away from here...', '12', '0', '100', '0', '0', '0', '23122', '0', 'Ancient Female Vrykul');
