INSERT INTO `creature_text` VALUES ('24248', '5', '0', 'But... It is not yet your time to serve the Lich King. Yes, a greater destiny awaits you. Power... You must become more powerful before you are to serve me.', '12', '0', '100', '1', '0', '14745', '23150', '0', 'The Lich King');
