INSERT INTO `creature_text` VALUES ('21504', '9', '0', 'And just what do you think you are doing?  You dare to interfere with my master\'s experiment?', '12', '0', '100', '25', '0', '0', '18369', '0', 'Pathaleon the Calculators Image');
INSERT INTO `creature_text` VALUES ('21504', '10', '0', 'Do you like what we\'ve done here?  Perhaps we will drop these crystals from the sky all over Outland.', '12', '0', '100', '6', '0', '0', '18370', '0', 'Pathaleon the Calculators Image');
INSERT INTO `creature_text` VALUES ('21504', '11', '0', 'I grow bored with your attempt to heal the land and quell the energies summoning and driving the colossi mad.  Goliathon, King of the Colossi, Prince Kael\'thas and I demand that you defend the crystal!', '12', '0', '100', '0', '0', '0', '18371', '0', 'Pathaleon the Calculators Image');
INSERT INTO `creature_text` VALUES ('21504', '12', '0', 'We will meet again soon.', '12', '0', '100', '1', '0', '0', '18372', '0', 'Pathaleon the Calculators Image');
INSERT INTO `creature_text` VALUES ('40423', '0', '0', '%s rallies the other combatants with a battle roar!', '41', '0', '100', '0', '0', '0', '40420', '0', 'Charscale Commander');
INSERT INTO `creature_text` VALUES ('16915', '0', '0', 'I\'ll get you working yet, you bucket of junk.', '12', '0', '100', '1', '0', '0', '17798', '0', 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES ('16915', '0', '1', 'What?!  You don\'t think I can do it?  I\'ll show you...once I get my parts, that is.', '12', '0', '100', '1', '0', '0', '17802', '0', 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES ('16915', '0', '2', 'It moved!  I swear, I saw it move!', '12', '0', '100', '0', '0', '0', '17804', '0', 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES ('16915', '0', '3', 'Ouch!  That\'s not right.', '12', '0', '100', '0', '0', '0', '17805', '0', 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES ('16915', '0', '4', 'Oh yeah, it\'s real funny isn\'t it?', '12', '0', '100', '1', '0', '0', '17803', '0', 'Foreman Razelcraz');
INSERT INTO `creature_text` VALUES ('16834', '0', '0', 'No!  Not... Sedai!  The orcs must pay!', '12', '0', '100', '1', '0', '0', '13997', '0', 'Anchorite Obadei');
INSERT INTO `creature_text` VALUES ('16834', '1', '0', 'What have you done, Makuru?!  These are not our ways!', '12', '0', '100', '0', '0', '0', '13991', '0', 'Anchorite Obadei');
INSERT INTO `creature_text` VALUES ('16834', '2', '0', 'I understand how you feel Makuru.  Sedai was my brother after all.  Yet we can\'t disgrace his memory by going against his very ideals.', '12', '0', '100', '0', '0', '0', '13992', '0', 'Makuru');
INSERT INTO `creature_text` VALUES ('16833', '1', '0', 'The orcs hate us, Obadei!  They\'ve killed many of us before!  They deserve death and worse.', '12', '0', '100', '0', '0', '0', '13996', '0', 'Makuru');
INSERT INTO `creature_text` VALUES ('19766', '0', '0', 'I don\'t know which is worse, getting eaten by fel orcs or working for that slave master Razelcraz! Oh well, thanks anyways!', '12', '0', '100', '66', '0', '0', '17410', '0', 'Jakk');
INSERT INTO `creature_text` VALUES ('19763', '0', '0', 'Thank goodness you got here, it was almost dinner time!', '12', '0', '100', '3', '0', '0', '17407', '0', 'Manni');
