INSERT INTO `creature_text` VALUES ('25222', '0', '4', 'Your profession?', '12', '7', '100', '0', '0', '0', '24384', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '1', '0', 'Yes, you\'re well seasoned in your field of work.  Report to the civilian liaison at once, we need more like you!', '12', '7', '100', '273', '0', '0', '24361', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '1', '1', 'I thought we had enough in your line of work, but it looks like we have some openings.  Report to the civilian liaison.', '12', '7', '100', '273', '0', '0', '24360', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '1', '2', 'I do have some openings in your line of work.  Report to the civilian liaison.', '12', '7', '100', '273', '0', '0', '24359', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '1', '3', 'Yeah.  We could use some more of you.  Report to the civilian liaison for work assignment.', '12', '7', '100', '273', '0', '0', '24358', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
