INSERT INTO `creature_text` VALUES ('25222', '2', '2', 'You\'re breaking my heart - I\'ve quotas to fill, you know?  Can you wield a sword?  Off to the barracks.', '12', '7', '100', '397', '0', '0', '24367', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '2', '3', 'With a sword arm like that?  I\'m afraid we need you in the front lines, my friend.  Report to the barracks.', '12', '7', '100', '397', '0', '0', '24366', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
INSERT INTO `creature_text` VALUES ('25222', '2', '4', 'Looks like we have room for one more... no, my mistake.  That\'s a seven not a one.  Hope you\'re good with a sword - report to the barracks.', '12', '7', '100', '397', '0', '0', '24368', '0', 'Recruitment Officer Carven to Generic Quest Trigger - LAB');
