INSERT INTO `creature_text` VALUES ('1428', '0', '0', 'Corbett, dear.  Harlan needs a load of knitted shirts and pants as soon as we can manage.', '12', '7', '100', '0', '0', '0', '253', '0', 'Rema Schneider');
