INSERT INTO `creature_text` VALUES ('1433', '0', '1', 'Business must be good down at the bazaar.  I\'ll get him resupplied right away!', '12', '7', '100', '0', '0', '0', '256', '0', 'Corbett Schneider');
INSERT INTO `creature_text` VALUES ('1433', '1', '0', 'Hm...after dropping this off, I think I\'ll head to that cheese shop for a snack.', '12', '7', '100', '0', '0', '0', '257', '0', 'Corbett Schneider');
INSERT INTO `creature_text` VALUES ('1433', '1', '1', 'I should have a few extra coins from this sale.  Maybe I\'ll buy myself some lunch...', '12', '7', '100', '0', '0', '0', '258', '0', 'Corbett Schneider');
INSERT INTO `creature_text` VALUES ('1433', '2', '0', 'Hey, Harlan.  Here\'s a load of knitted cloth for you.', '12', '7', '100', '0', '0', '0', '259', '0', 'Corbett Schneider');
INSERT INTO `creature_text` VALUES ('1433', '2', '1', 'Oomph!  Here\'s another load of supplies, Harlan.  It must be selling fast!', '12', '7', '100', '0', '0', '0', '260', '0', 'Corbett Schneider');
INSERT INTO `creature_text` VALUES ('1433', '3', '0', 'Well, I\'m off then.  Take care, Harlan.', '12', '7', '100', '0', '0', '0', '263', '0', 'Corbett Schneider');
